import { collection, onSnapshot } from "firebase/firestore";
import { PieChart, Pie } from "recharts";
import db from "../../firebase/firestore";
import React, { useEffect, useState } from "react";
import './PollResults.css';



export default function PollResults({ pollID }) {

    let [title, setTitle] = useState('');
    let [options, setOptions] = useState(['']);
    let [results, setResults] = useState([{}]);
    let [isLoading, setLoadingStatus] = useState(true);

    useEffect(() => {
        onSnapshot(collection(db, 'polls'), (snapshot) => {
            snapshot.docs.map((doc) => {
                if(doc.id === pollID) {
                    const data = doc.data();
                    let totalVotes = data.optionACount + data.optionBCount + data.optionCCount + data.optionDCount;
                    let votePercentageACalc = data.optionACount / totalVotes * 100;
                    let votePercentageBCalc = data.optionBCount / totalVotes * 100;
                    let votePercentageCCalc = data.optionCCount / totalVotes * 100;
                    let votePercentageDCalc = data.optionDCount / totalVotes * 100;
                    const results = [
                        {
                            "name": data.options[0],
                            "value": votePercentageACalc,
                        },
                        {
                            "name": data.options[1],
                            "value": votePercentageBCalc,
                        },
                        {
                            "name": data.options[2],
                            "value": votePercentageCCalc,
                        },
                        {
                            "name": data.options[3],
                            "value": votePercentageDCalc,
                        },
                    ]
                    setTitle(data.title);
                    setOptions(data.options);
                    setResults(results);
                    setTimeout(() => { setLoadingStatus(false); }, 1200);
                }
            })
        })
    }, [results]);

    
    return (
        <div>
            {!isLoading ? (
                <div>
                    <h1>{title}</h1>
                    <PieChart width={500} height={250}>
                        <Pie data={data01} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={50} fill="#8884d8" />
                    </PieChart>
                </div>
            ) : (
                <h1>Loading!</h1>
            )}
        </div>
    )
}
