import { collection, onSnapshot } from "firebase/firestore";
import { PieChart, Pie, Cell, ResponsiveContainer } from "recharts";
import db from "../../firebase/firestore";
import React, { useEffect, useState } from "react";
import './PollResults.css';





export default function PollResults({ pollID }) {

    let [title, setTitle] = useState('');
    let [options, setOptions] = useState(['']);
    let [results, setResults] = useState([{}]);
    let [isLoading, setLoadingStatus] = useState(true);

    useEffect(() => {
        onSnapshot(collection(db, 'polls'), (snapshot) => {
            snapshot.docs.map((doc) => {
                if(doc.id === pollID) {
                    const data = doc.data();
                    let totalVotes = data.optionACount + data.optionBCount + data.optionCCount + data.optionDCount;
                    let votePercentageACalc = data.optionACount / totalVotes * 100;
                    let votePercentageBCalc = data.optionBCount / totalVotes * 100;
                    let votePercentageCCalc = data.optionCCount / totalVotes * 100;
                    let votePercentageDCalc = data.optionDCount / totalVotes * 100;
                    const results = [
                        {
                            "name": data.options[0],
                            "value": votePercentageACalc,
                        },
                        {
                            "name": data.options[1],
                            "value": votePercentageBCalc,
                        },
                        {
                            "name": data.options[2],
                            "value": votePercentageCCalc,
                        },
                        {
                            "name": data.options[3],
                            "value": votePercentageDCalc,
                        },
                    ]
                    setTitle(data.title);
                    setOptions(data.options);
                    setResults(results);
                    setTimeout(() => { setLoadingStatus(false); }, 1200);
                }
            })
        })
    }, [results]);

    const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042"];

const RADIAN = Math.PI / 180;
const renderCustomizedLabel = ({
  cx,
  cy,
  midAngle,
  innerRadius,
  outerRadius,
  percent,
  index
}: any) => {
  const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
  const x = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy + radius * Math.sin(-midAngle * RADIAN);

  return (
    <text
      x={x}
      y={y}
      fill="white"
      textAnchor={x > cx ? "start" : "end"}
      dominantBaseline="central"
    >
      {`${(percent * 100).toFixed(0)}%`}
    </text>
  );
};
    
    return (
        <div>
            {!isLoading ? (
                <div>
                    <h1>{title}</h1>
                    <PieChart width={400} height={400}>
                        <Pie data={results} cx={200} cy={200} labelLine={false} label={renderCustomizedLabel}
                            outerRadius={80} fill="#8884d8" dataKey="value">
                            {results.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                            ))}
                        </Pie>
    </PieChart>
                </div>
            ) : (
                <h1>Loading!</h1>
            )}
        </div>
    )
}
