import react, { useEffect, useState } from "react";
import Results from "../VoteResults/Results";


export default function Vote({title, options}) {

    let [optionOneCount, setOptionOneCount] = useState(0);
    let [optionTwoCount, setOptionTwoCount] = useState(0);
    let [optionThreeCount, setOptionThreeCount] = useState(0);
    let [optionFourCount, setOptionFourCount] = useState(0);

    useEffect(() => {
        let totalVotes = optionOneCount + optionTwoCount + optionThreeCount + optionFourCount;
        let votePercentage = optionOneCount / totalVotes * 100;
        console.log(`${votePercentage}% voted for Chenzo`);
    });

    return (
        <div>
            <h1>{title}</h1>
            <button onClick={() => setOptionOneCount(optionOneCount + 1)}>{options[0]}</button><br></br>
            <button onClick={() => setOptionTwoCount(optionTwoCount + 1)}>{options[1]}</button><br></br>
            <button onClick={() => setOptionThreeCount(optionThreeCount + 1)}>{options[2]}</button><br></br>
            <button onClick={() => setOptionFourCount(optionFourCount + 1)}>{options[3]}</button><br></br>
        </div>
    )
}
