import { collection, onSnapshot } from "firebase/firestore";
import db from "../../firebase/firestore";
import React, { useEffect, useState } from "react";
import './PollResults.css';



export default function PollResults({ pollID }) {

    let [title, setTitle] = useState('');
    let [options, setOptions] = useState([]);
    let [optionOneCount, setOptionOneCount] = useState(0);
    let [optionTwoCount, setOptionTwoCount] = useState(0);
    let [optionThreeCount, setOptionThreeCount] = useState(0);
    let [optionFourCount, setOptionFourCount] = useState(0.00);
    let [votePercentageA, setVotePercentageA] = useState(0.00);
    let [votePercentageB, setVotePercentageB] = useState(0.00);
    let [votePercentageC, setVotePercentageC] = useState(0.00);
    let [votePercentageD, setVotePercentageD] = useState(0.00);

    useEffect(() => {
        onSnapshot(collection(db, 'polls'), (snapshot) => {
            snapshot.docs.map((doc) => {
                if(doc.id === pollID) {
                    const data = doc.data();
                    setTitle(data.title);
                    setOptions(data.options);
                    setOptionOneCount(data.optionACount);
                    setOptionTwoCount(data.optionBCount);
                    setOptionThreeCount(data.optionCCount);
                    setOptionFourCount(data.optionDCount);
                }
            })
            let totalVotes = optionOneCount + optionTwoCount + optionThreeCount + optionFourCount;
            setVotePercentageA(Math.trunc((optionOneCount / totalVotes * 100)));
            setVotePercentageB(Math.trunc((optionTwoCount / totalVotes * 100)));
            setVotePercentageC(Math.trunc((optionThreeCount / totalVotes * 100)));
            setVotePercentageD(Math.trunc((optionFourCount / totalVotes * 100)));
            console.log(`${votePercentageA}% voted for ${options[0]}`);
            console.log(`${votePercentageB}% voted for ${options[1]}`);
            console.log(`${votePercentageC}% voted for ${options[2]}`);
            console.log(`${votePercentageD}% voted for ${options[3]}`);
        })
    }, []);

    
    return (
        <div>
            <h1>{title}</h1>
            <div className="bar">
                <div className="progress one"></div>
                <div className="percent">{votePercentageA}</div>
                <div className="text">{options[0]}</div>
            </div>
            <div className="bar">
                <div className="progress two"></div>
                <div className="percent">{votePercentageB}</div>
                <div className="text">{options[1]}</div>
            </div>
            <div className="bar">
                <div className="progress three"></div>
                <div className="percent">{votePercentageC}</div>
                <div className="text">{options[2]}</div>
            </div>
            <div className="bar">
                <div className="progress four"></div>
                <div className="percent">{votePercentageD}</div>
                <div className="text">{options[3]}</div>
            </div>
        </div>
    )
}
