import { collection, onSnapshot } from "firebase/firestore";
import { PieChart, Pie } from "recharts";
import db from "../../firebase/firestore";
import React, { useEffect, useState } from "react";
import './PollResults.css';



export default function PollResults({ pollID }) {

    let [title, setTitle] = useState('');
    let [options, setOptions] = useState([]);
    let [optionOneCount, setOptionOneCount] = useState(0);
    let [optionTwoCount, setOptionTwoCount] = useState(0);
    let [optionThreeCount, setOptionThreeCount] = useState(0);
    let [optionFourCount, setOptionFourCount] = useState(0);
    let [results, setResults] = useState([0]);
    let [resultsStatus, setResultStatus] = useState(false);

    useEffect(() => {
        let totalVotes = optionOneCount + optionTwoCount + optionThreeCount + optionFourCount;
        let votePercentageACalc = optionOneCount / totalVotes * 100;
        let votePercentageBCalc = optionTwoCount / totalVotes * 100;
        let votePercentageCCalc = optionThreeCount / totalVotes * 100;
        let votePercentageDCalc = optionFourCount / totalVotes * 100;
        const results = [votePercentageACalc, votePercentageBCalc, votePercentageCCalc, votePercentageDCalc];
        setResults(results);
        console.log(`${results[0]}% voted for ${options[0]}`);
        console.log(`${results[1]}% voted for ${options[1]}`);
        console.log(`${results[2]}% voted for ${options[2]}`);
        console.log(`${results[3]}% voted for ${options[3]}`);
        onSnapshot(collection(db, 'polls'), (snapshot) => {
            snapshot.docs.map((doc) => {
                if(doc.id === pollID) {
                    const data = doc.data();
                    setTitle(data.title);
                    setOptions(data.options);
                    setOptionOneCount(data.optionACount);
                    setOptionTwoCount(data.optionBCount);
                    setOptionThreeCount(data.optionCCount);
                    setOptionFourCount(data.optionDCount);
                    setResultStatus(true);
                }
            })
        })
    }, []);

    
    return (
        <div>
            {resultsStatus ? (
                <div>
                    <h1>{title}</h1>
                    <PieChart width={500} height={250}>
                        <Pie data={results} dataKey="value" nameKey="name" cx="50%" cy="50%" innerRadius={60}
                            outerRadius={80} fill="#82ca9d" label />
                    </PieChart>
                </div>
            ) : (
                <h1>Couldnt load poll data!</h1>
            )}
        </div>
    )
}
